import React,{ useState, useEffect } from "react";
import YourBotArmy from "./YourBotArmy";
import BotCollection from "./BotCollection";

function BotsPage() {
  //start here with your code for step one
  const [bots , setBots ] = useState([])

   // Function to fetch bot data from the backend
   const fetchBots = async () => {
    try {
      const response = await fetch(`http://localhost:8002/bots `);
      if (!response.ok) {
        throw new Error('Failed to fetch bots');
      }
      const data = await response.json();
      setBots(data); // Update state with fetched bot data
    } catch (error) {
      console.error('Error fetching bots:', error);
      // Handle error , show error message to the user, retry fetching, etc.
    }
    // Fetch bots data when the component mounts
  useEffect(() => {
    fetchBots();
  }, []); // Empty dependency array ensures useEffect runs only once after initial render

  };
  return (
    <div>
      <YourBotArmy />
      <BotCollection bots={bots} />
    </div>
  )
}

export default BotsPage;
