import React from "react";

const botTypeClasses = {
  Assault: "icon military",
  Defender: "icon shield",
  Support: "icon plus circle",
  Medic: "icon ambulance",
  Witch: "icon magic",
  Captain: "icon star",
};

function BotCard({ bot, onSelect , onDischarge, IamRendering }) {

  function handleDischargeBot (botId) {
  fetch(`http://localhost:8002/bots/${botId}`, {
    method: 'DELETE',
    headers: {
      "Content-Type": "application/json",
    },
  })
     .then((res) => {
      if (res.ok) {
        onDischarge(botId)
      }else {
        throw new Error("Failed to discharge")
      }
     })
     .catch((error) => {
      console.error("Error removing bot:", error);
  })

  };
  return (
    <div className="ui column">
      <div
        className="ui card"
        key={bot.id}
        onClick={ () => (IamRendering ? onDischarge(bot.id) : onSelect(bot))}
      >
        <div className="image">
          <img alt="oh no!" src={bot.avatar_url} />
        </div>
        <div className="content">
          <div className="header">
            {bot.name}
            <i className={botTypeClasses[bot.bot_class]} />
          </div>
          <div className="meta text-wrap">
            <small>{bot.catchphrase}</small>
          </div>
        </div>
        <div className="extra content">
          <span>
            <i className="icon heartbeat" />
            {bot.health}
          </span>

          <span>
            <i className="icon lightning" />
            {bot.damage}
          </span>
          <span>
            <i className="icon shield" />
            {bot.armor}
          </span>
          <span>
            <div className="ui center aligned segment basic">
              <button
                className="ui mini red button"
                onClick={() => handleDischargeBot(bot)}
              >
                x
              </button>
            </div>
          </span>
        </div>
      </div>
    </div>
  );
}

export default BotCard;
