import React,{ useState, useEffect } from "react";
import YourBotArmy from "./YourBotArmy";
import BotCollection from "./BotCollection";

function BotsPage() {
  //start here with your code for step one
  const [bots , setBots ] = useState([]);
  const [army , setArmy ] = useState([]);


  // Fetch bots data from backend
  useEffect(() => {
    const fetchBots = async () => {
      try {
        const response = await fetch('http://localhost:8002/bots');
        if (!response.ok) {
          throw new Error('Failed to fetch bots');
        }
        const data = await response.json();
        setBots(data);
      } catch (error) {
        console.error('Error fetching bots:', error);
      }
    };

    fetchBots();
  }, []);

  // Function to add a bot to the army
  const addToArmy = (bot) => {
    if (!army.some(b => b.id === bot.id)) {
      setArmy([...army, bot]);
    }
  };

  // Function to remove a bot from the army
  const dischargeFromArmy = (botId) => {
    const updatedArmy = army.filter(bot => bot.id !== botId);
    setArmy(updatedArmy);
  };
  return (
    <div>
      <YourBotArmy army={army} onAddToArmy={addToArmy} />
      <BotCollection bot={bots} onDischarge={dischargeFromArmy} />
    </div>
  )
}

export default BotsPage;
